import { eventWrapper } from "@testing-library/user-event/dist/utils";
import React from "react";

export default function Work(props){

    const [nota, setNota] = React.useState(
        {
            title: "",
            content: ""
        }
    );

    function handleChange(event){

       const {nome, valor} = event.target
        setNota(
            function prevNote(){
                return {
                    ...prevNote,
                    [nome]:valor
                }
            }
        )}

        const [notas, setNotas] = React.useState([]);

    function addNota(nota) {
            setNotas((prevNotas) => {
              return [...prevNotas, nota];
            });
          }

    props.onAdd = addNota(nota);

    function submitNota(event){
       addNota(nota);
        setNota({
            tarefa:"",
            texto:""
        });
        event.preventDefault();


    }

    return(
        <div>
            <form>
            <input 
            onChange={handleChange}
            nome= "work"
            value={nota.work}
            placeholder= "Title"
            >

            </input>

            <textarea
            onChange={handleChange}
            nome="description"
            value={nota.description}
            placeholder="Describe your work"
            row="3"
            >

            </textarea>

            <button onClick={submitNota}>Adicionar</button>
            </form>
        </div>
    )


}
