import React from "react";

export default function Work(props){

    const [nota, setNota] = React.useState(
        {
            title: "",
            content: ""
        }
    );

    function handleChange(event){

       const {name, value} = event.target;
        setNota(
            function prevNota(){
                return {
                    ...prevNota,
                    [name]:value
                }
            }
        )}

     
    function submitNota(event){
        props.onAdd(nota);
        setNota({
            work:"",
            description:""
        });
        event.preventDefault();


    }

    return(
        <div>
            <form>
            <input 
            onChange={handleChange}
            name= "work"
            value={nota.work}
            placeholder= "Title"
            >

            </input>

            <textarea
            onChange={handleChange}
            name="description"
            value={nota.description}
            placeholder="Describe your work"
            row="3"
            >

            </textarea>

            <button onClick={submitNota}>Adicionar</button>
            </form>
        </div>
    )


}
