import React from "react";
import Header from "./Header";
import Footer from "./Footer";
import Work from "./Work";
import Note from "./Note";



export default function App(props) {

  const [notas, setNotas] = React.useState([]);

  function addNota(note) {
    setNotas((prevNotas) => {
      return [...prevNotas, note];
    });
  }

  function deletarNota(id) {
    setNotas((prevNotas) => {
      return prevNotas.filter((notasId, Id) => {
        return Id !== id;
      });
    });
  }

  return (
    <div>
      <Header />
      <Work onAdd={addNota} />

      {notas.map((noteId, Id) => {
        return (
          <Note
            key={Id}
            id={Id}
            titulo={noteId.tarefa}
            descricao={noteId.texto}
            onDelete={deletarNota}
          />
        );
      })}

      <Footer />
    </div>
  );
}
