import React from "react";

export default function Header(){

    
    const agora = new Date().toLocaleTimeString();
    
    const [time, setTime] = React.useState(agora);
    const year = new Date().getFullYear();
    const m = ["Janeiro", "Fevereiro", "Março", "Abril", "Maio", "Junho","Julho", "Agosto", "Setembro", "Aoutubro", "Novembro", "Dezembro"]
    const date = m[new Date().getMonth()];

    function updateDate(event){
        
        const {name, value} = event.target;
        const agora = new Date().toLocaleTimeString();
          
        setTime(agora);
    } 
    
         

    return(
        <header>
            <h1 onChange={updateDate}>{time}, {date} de {year}</h1>
        </header>
    )
}
