export default function Header(){

    const m = ["Janeiro", "Fevereiro", "Março", "Abril", "Maio", "Junho","Julho", "Agosto", "Setembro", "Aoutubro", "Novembro", "Dezembro"]
    const date = new Date().getDay();
    const agora = new Date().toLocaleTimeString({ month: 'long' });
    const [{hora, data}, setHora] = React.useState({agora, data});
    
    
    

    function updateTime(){
        const agora = new Date().toLocaleTimeString();
        setHora(agora);
    } 
    
         

    return(
        <header>
            <h1>{hora}, {data}, </h1>
        </header>
    )
}
