import React from "react";

export default function Work(){

    const [nota, setNota] = React.useState(
        {
            title: "",
            content: ""
        }
    );

    function handleChange(event){

       const {nome, valor} = event.target
        setNota(
            function prevNote(){
                return {
                    ...prevNote,
                    [nome]:valor
                }
            }
        )
    }


    return(
        <div>
            <form>
            <input 
            onChange={handleChange}
            nome= ""
            value={nota.tarefa}
            >

            </input>

            <textarea>

            </textarea>

            <button>Adicionar</button>
            </form>
        </div>
    )


}
